-- 1. Criar a tabela de Ligas
CREATE TABLE public.leagues (
    id uuid NOT NULL DEFAULT gen_random_uuid(),
    name text NOT NULL,
    slug text NOT NULL,
    created_at timestamp with time zone NOT NULL DEFAULT now(),
    CONSTRAINT leagues_pkey PRIMARY KEY (id),
    CONSTRAINT leagues_name_key UNIQUE (name),
    CONSTRAINT leagues_slug_key UNIQUE (slug)
);

-- 2. Criar a tabela de Times
CREATE TABLE public.teams (
    id uuid NOT NULL DEFAULT gen_random_uuid(),
    name text NOT NULL,
    price numeric NOT NULL,
    image1 text NOT NULL,
    image2 text NOT NULL,
    league_id uuid NOT NULL,
    created_at timestamp with time zone NOT NULL DEFAULT now(),
    CONSTRAINT teams_pkey PRIMARY KEY (id),
    CONSTRAINT teams_league_id_fkey FOREIGN KEY (league_id) REFERENCES public.leagues(id) ON DELETE CASCADE
);

-- 3. Habilitar RLS e criar políticas de leitura pública
ALTER TABLE public.leagues ENABLE ROW LEVEL SECURITY;
CREATE POLICY "Allow public read access to leagues" ON public.leagues FOR SELECT USING (true);

ALTER TABLE public.teams ENABLE ROW LEVEL SECURITY;
CREATE POLICY "Allow public read access to teams" ON public.teams FOR SELECT USING (true);

